/** * https://leetcode.com/problems/length-of-last-word/description/ * 2015/07 * 0 ms */int lengthOfLastWord(char * s){    int length = 0;    int idx = strlen(s) - 1;    while(s[idx] == ' ')        idx --;        for(; idx >= 0; idx --)    {        if((s[idx] >= 'a' && s[idx] <= 'z') || (s[idx] >= 'A' && s[idx] <= 'Z'))        {            length ++;        }        else        {            break;        }    }    return length;}