/** * https://leetcode.com/problems/remove-k-digits/description/ * 2016/10 * 9 ms */char * removeKdigits(char * num, int k){    int max_len = strlen(num) + 1;    char * min_str = (char *)malloc(sizeof(char) * max_len);    if(strlen(num) <= k)    {        snprintf(min_str, 2, "0");        return min_str;    }        int begin = 0;    int min_bit = 0;    while(k > 0)    {        int min_idx = begin;        char min_char = num[begin];        for(int idx = begin; idx <= begin + k; idx ++)        {            if(num[idx] < min_char)            {                min_idx = idx;                min_char = num[idx];            }        }        min_str[min_bit ++] = min_char;        k -= min_idx - begin;        begin = min_idx + 1;    }    while(num[begin] != '\0')    {        min_str[min_bit ++] = num[begin ++];    }    min_str[min_bit] = '\0';    char * trimed = (char *)malloc(sizeof(char) * (strlen(min_str) + 1));    char * str = min_str;    while('0' == * str)    {        str ++;    }    sprintf(trimed, str);    if(strlen(trimed) < 1)    {        sprintf(trimed, "0");    }    free(min_str);    return trimed;}