/** * https://leetcode.com/problems/peak-index-in-a-mountain-array/description/ * 2018/07 * 8 ms */class Solution{public:    int peakIndexInMountainArray(vector<int> & A)    {        int start = 0;        int end = A.size();        while(end - start > 8)        {            int mid = (start + end) / 2;            if(A[mid] > A[mid - 1])            {                start = mid - 1;            }            else            {                end = mid + 1;            }        }        for(int idx = start; idx + 1 < end; idx ++)        {            if(A[idx] > A[idx + 1])            {                return idx;            }        }        return -1;    }};